import React from 'react';
import ReactDOM from 'react-dom';

import './index.css';

import Canvas from './components/Canvas/Canvas';

class Grid {
    constructor(rows, cols, width, colours=['#FFFFFF', '#000000']) {
        this.rows = rows;
        this.cols = cols;
        this.width = width;
        this.colours = colours;
        this.grid = (new Array(rows)).fill(0).map(() => (new Array(cols)).fill(0));
    }

    getGrid() { return this.grid; }
    getVal(row, col) { return this.grid[row][col]; }
    setVal(row, col, val) { this.grid[row][col] = val; }
    getColour(row, col) { return this.colours[Math.min(this.colours.length-1, this.grid[row][col])]; }

    render(context, fc=null) {
        const n = this.rows, m = this.cols;
        for(let i=0; i<n; ++i) for(let j=0; j<m; ++j) {
            context.fillStyle = getColour(i, j);

        }

    };

    printGrid() {
        const n = this.rows;
        const m = this.cols;
        let output = "";
        for(let i=0; i<n; ++i) {
            for(let j=0; j<m; ++j) output += this.grid[i][j] + ' ';
            output += '\n';
        }
        console.log(output);
    };
};

const board = new Grid(10, 10, 10);
board.printGrid();

const draw = (context, fc) => {
    context.clearRect(0, 0, window.innerWidth, window.innerHeight);
    board.render(context);
};

ReactDOM.render(<Canvas draw={draw} />, document.getElementById('root'));
