import React from 'react';
import ReactDOM from 'react-dom';

import Canvas from './components/Canvas/Canvas';

const { write } = process.stdout;

class Grid {
    constructor(rows, cols, width, height, square=false) {
        this.rows = rows; this.cols = cols; this.width = width; this.height = height;
        this.grid = (new Array(rows)).fill(0).map(() => (new Array(cols)).fill(0));
    }

    render() {

    };

    printGrid() {
        for(let i=0; i<this.rows; ++i) {
            for(let j=0; j<this.cols; ++j) {
                write(this.grid[i][j]);
                write(' ');
            }
            write('\n');
        }
    };
};

const board = new Grid(10, 10, window.innerWidth, window.innerHeight, false);
board.printGrid();

