import React from 'react';
import ReactDOM from 'react-dom';

import './index.css';

import Canvas from './components/Canvas/Canvas';

class Grid {
    constuctor(rows, cols, width, colours = ["#FFFFFF", "#000000"]) {
        this.rows = rows; this.cols = cols; this.width = width;
        this.grid = new Array(rows).fill(0).map(() => new Array(cols).fill(0));
    }

    getGrid() { return this.grid; }

    getCol(row, col) {
        return this.colours[Math.min(this.grid[row][col], this.colours.length-1)];
    }

    getPos(row, col) {
        return [this.row * this.width, this.col * this.width];
    }

    render(context, fc=null) {
        const n = this.rows, m = this.cols;
        for(let i=0; i<n; ++i) for(let j=0; j<m; ++j) {
            context.fillStyle = this.getCol(i, j);
            context.fillRect(...this.getPos(i, j), this.width, this.width);
        }
    }
}


const board = new Grid(10, 10, 10);
board.setVal(0,0,1);
board.printGrid();

const draw = (context, fc) => {
    context.clearRect(0, 0, window.innerWidth, window.innerHeight);
    board.render(context);
};

ReactDOM.render(<Canvas draw={draw} />, document.getElementById('root'));
